import { useState } from "react";
import BookCreate from "./components/BookCreate";

function App() {
  const [books, setBooks] = useState([]);

  const createBook = (title) => {
    // it's going to receive an argument of whatevr title the user puts in.So when we call createBook we need to make sure we use as an argument the title the user input
    const updatedBooks = [
      ...books,
      { id: Math.round(Math.random() * 9999), title }, // each book will have a random id
    ]; // could write title: tile, but JS allows us to just use title instead in the new object refering to the new title the user will add
    setBooks(updatedBooks);
  };

  return (
    <div>
      {books.length}
      <BookCreate onCreate={createBook} />
    </div>
  );
}

export default App;
