import { useState } from "react"; // 2

function BookCreate({ onCreate }) {
  // 1 ---> it receives the prop onCreate which was created already in App.js
  const [title, setTitle] = useState(""); // 3 --> the arg is an empty string as by default the input will appear empty
  //  4
  const handleChange = (event) => {
    setTitle(event.target.value); // we want to look at what the user typed in the input el. i.e. the event!!
  };

  // almost always use value and props like onChange in the input element!

  // 5
  const handleSubmit = (event) => {
    event.preventDefault();
    onCreate(title); // onCreate we pass in the title the user entered
    setTitle(""); // updating our piece of state by setting it to empty string --> emptying the text that user types automatically instead of manually
  };

  return (
    <div className="book-create">
      <form onSubmit={handleSubmit}>
        <label>Title</label>
        <input value={title} onChange={handleChange} />
        <button>Create!</button>
      </form>
    </div>
  );
}

export default BookCreate;
