import { useState } from "react";
import axios from "axios";
import BookCreate from "./components/BookCreate";
import BookList from "./components/BookList";

function App() {
  const [books, setBooks] = useState([]);

  const editBookById = (id, newTitle) => {
    const updatedBooks = books.map((book) => {
      if (book.id === id) {
        // if the book's id is = to the id that has been passed into our overall function that means this is the book object we want to update
        return { ...book, title: newTitle };
      }
      return book; // if not, return the original book
    });

    setBooks(updatedBooks);
  };

  const deleteBookById = (id) => {
    // takes the id of book that we want to delete and pass it down therough the props system to BookList component
    // then from BookList comp. we forward that function the every copy of BookShow
    const updatedBooks = books.filter((book) => {
      return book.id !== id; // if the book is not eq ual to the id
    });

    setBooks(updatedBooks);
  };

  const createBook = async (title) => {
    const response = await axios.post("http://localhost:3001/books", {
      title,
    });

    // it's going to receive an argument of whatever title the user puts in.So when we call createBook we need to make sure we use as an argument the title the user input
    const updatedBooks = [...books, response.data]; // could write title: tile, but JS allows us to just use title instead in the new object refering to the new title the user will add
    setBooks(updatedBooks);
  };

  return (
    <div className="app">
      <h1>Reading List</h1>
      <BookList onEdit={editBookById} books={books} onDelete={deleteBookById} />
      <BookCreate onCreate={createBook} />
    </div>
  );
}

export default App;
