import { useState } from "react";

function BookCreate(onCreate) {
  // it receives the prop onCreate which was created already in App.js
  const [title, setTitle] = useState(""); // the arg is an empty string as by default the input will appear empty

  const handleChange = (event) => {
    setTitle(event.target.value); // we want to look at what the user typed i.e. the event!!
  };

  return (
    <div>
      <form>
        <label>Title</label>
        <input value={title} onChange={handleChange} />
      </form>
    </div>
  );
}

export default BookCreate;
